mapper.select:
select * from rsss."users"
where
    validity is true

        and id = #{id}


        and username = #{username}

mapper.update:
update rsss."users" set
    password = #{password},
    modified = now(),
    permissions = #{permissions},
    validity = #{validity}
    where id = #{id}

update rsss."users" set
     lastaccess= now()
     where id = #{id}
